import Vue from 'vue'

var logged_user = null;

function mockasync (data) {
  return new Promise((resolve, reject) => {
    setTimeout(() => resolve({data: data}), 600)
  })
}

const api = {
    login(username, password){
        if(password){
            logged_user = {
                username: username,
                first_name: 'Mark',
                last_name: 'Zuckerberg',
                email: 'zuck@facebook.com',
                notifications_enabled: true,
                permissions:{
                    ADMIN: username == 'admin',
                    STAFF: username == 'admin',
                }
            };
        }
        return mockasync(logged_user);
    },
    logout(){
        logged_user = null;
        return mockasync({});
    },
    whoami(){
        return mockasync(logged_user ? {
            authenticated: true,
            user: logged_user,
        } : {authenticated: false});
    },
    add_todo(newtask){
        return mockasync({description: newtask, done: false});
    },
    list_todos(){
        return mockasync({
            todos: [
                {description: 'Do the laundry', done: true},
                {description: 'Walk the dog', done: false}
            ]
        });
    },
    list_posts(username){
        const d = new Date()
        const _1min = 60000
        const _1h = 60 * _1min
        const d1 = new Date(d - 15 * _1min)
        const d2 = new Date(d - 2 * _1h)
        const d3 = new Date(d - 48 * _1h)
        return mockasync([
            {
              id: '1',
              author_name: 'Isaacc Newton',
              author_username: '@isaacnewton',
              author_avatar: `https://img.itdg.com.br/tdg/images/recipes/000/002/744/325185/325185_original.jpg?mode=crop&width=710&height=400`,
              created_at: d3.toISOString(),
              prato: 'Strogonoff',
              introducao: `Introdução`,
              ingredientes: `Ingredientes`,
              preparo: `Modo de preparo`
            },
            {
                id: '2',
                author_name: 'Isaacc Newton',
                author_username: '@isaacnewton',
                author_avatar: `https://img.itdg.com.br/tdg/images/recipes/000/002/744/325185/325185_original.jpg?mode=crop&width=710&height=400`,
                created_at: d3.toISOString(),
                prato: 'Bolo de milho',
                introducao: `Introdução`,
                ingredientes: `Ingredientes`,
                preparo: `Modo de preparo`
            },
        ])
    },
    list_tweets(username){
        const d = new Date()
        const _1min = 60000
        const _1h = 60 * _1min
        const d1 = new Date(d - 15 * _1min)
        const d2 = new Date(d - 2 * _1h)
        const d3 = new Date(d - 48 * _1h)
        return mockasync([
            {
              id: '1',
              author_name: 'Isaacc Newton',
              author_username: username || '@isaacnewton',
              author_avatar: `data:image/jpeg;base64,`,
              created_at: d1.toISOString(),
              text: `todo corpo permanece em seu estado de repouso ou em movimento 
                  retilíneo e uniforme caso as forças que atuem sobre ele se anulem.`
            },
            {
              id: '2',
              author_name: 'René Descartes',
              author_username: username || '@descartes',
              author_avatar: `data:image/jpeg;base64,`,
              created_at: d2.toISOString(),
              text: `todo corpo permanece em seu estado de repouso ou em movimento 
                  retilíneo e uniforme caso as forças que atuem sobre ele se anulem.`
            },
            {
              id: '3',
              author_name: 'Albert Einsten',
              author_username: username || '@einsten',
              author_avatar: `data:image/jpeg;base64,`,
              created_at: d3.toISOString(),
              text: `todo corpo permanece em seu estado de repouso ou em movimento 
                  retilíneo e uniforme caso as forças que atuem sobre ele se anulem.`
            },
        ])
    }
};

export default api;
